//Content of dropdown menu\\n.profile-dropdown {\\n    width: 350px;\\n    height: 240px;\\n    display: flex;\\n    flex-direction: column;\\n    justify-content: center;\\n    align-items: center;\\n\\n    //Profile image inside of dropdown menu\\n    .avatar-header-image {\\n        height: 48px;\\n        width: 48px;\\n        border: solid 3px $white;\\n        font-size: 20px;\\n        color: $white;\\n        align-items: center;\\n        border-radius: 50%;\\n        background-color: $tertiary-500;\\n        display: flex;\\n        flex-direction: column;\\n        justify-content: space-evenly;\\n        margin-bottom: 10px;\\n    }\\n}\\n\\n.profile-dropdown-divider {\\n    margin-top: 16px;\\n    margin-bottom: 8px;\\n    width: 300px;\\n    justify-content: center;\\n    align-items: center;\\n}\\n\\n.profile-log-out {\\n    margin: 15px;\\n    width: fit-content;\\n}\\n\\n.statement-links {\\n    display: flex;\\n    align-items: center;\\n    text-align: center;\\n    font-size: 12px;\\n\\n    //The dot icon between \'Privacy policy\' and \'Terms of service\'\\n    .bi-dot {\\n        size: 12px;\\n        color: $gray-700;\\n        font-weight: 400;\\n    }\\n\\n    a {\\n        margin-right: 8px;\\n        margin-left: 8px;\\n    }\\n}\\n\\n// Removes NgbDropdown arrow\\n.dropdown-toggle::after {\\n    display: none;\\n}\\n\\n.tools-navigation-container {\\n    margin-top: 5px;\\n}\\n\\n.sub-header {\\n    // min-height: 56px;\\n    height: 56px;\\n    // padding-top: 5px;\\n    // box-shadow: 0px 0px 2px $gray-500;\\n    background-color: $gray-100;\\n    z-index: -1;\\n    // padding: 0 0;\\n    padding-left: 24px;\\n    display: flex;\\n    box-sizing: border-box;\\n    width: 100%;\\n    flex-direction: row;\\n    align-items: center;\\n    white-space: nowrap;\\n\\n    .row {\\n        --bs-gutter-x: 0;\\n    }\\n}\\n\\n.tools-navigation-container {\\n    flex-direction: row;\\n    box-sizing: border-box;\\n    display: flex;\\n    place-content: center flex-end;\\n    align-items: center;\\n    height: 40px;\\n}\\n\\n\\n.hide-alert {\\n    animation: fade-anim 0.3s 1;\\n    -webkit-animation: fade-anim 0.3s 1;\\n    animation-fill-mode: forwards;\\n\\n    animation-delay: 0s;\\n    -webkit-animation-delay: 0s;\\n    /* Safari and Chrome */\\n    -webkit-animation-fill-mode: forwards;\\n}\\n\\n.btn:focus {\\n    box-shadow: none;\\n}\\n\\n@keyframes fade-anim {\\n    from {\\n        opacity: 1;\\n    }\\n\\n    to {\\n        opacity: 0;\\n    }\\n}\\n\\n@-webkit-keyframes fade-anim {\\n    from {\\n        opacity: 1;\\n    }\\n\\n    to {\\n        opacity: 0;\\n    }\\n}\\n\\n.pendo-resource-center {\\n    position: absolute; \\n    bottom: 10px; \\n    left: 10px; \\n    max-height: 50px; \\n    max-width: 50px; \\n    z-index: 18999;\\n}","styleUrl":"./layout.component.scss"}],"stylesData":"","constructorObj":{"name":"constructor","description":"","deprecated":false,"deprecationMessage":"","args":[{"name":"layoutService","type":"UiLayoutService","deprecated":false,"deprecationMessage":""},{"name":"pageHeaderService","type":"UiPageHeaderService","deprecated":false,"deprecationMessage":""},{"name":"pageFooterService","type":"UiPageFooterService","deprecated":false,"deprecationMessage":""},{"name":"modalService","type":"UiModalService","deprecated":false,"deprecationMessage":""},{"name":"_translateService","type":"TranslateService","deprecated":false,"deprecationMessage":""},{"name":"_headerService","type":"UiHeaderService","deprecated":false,"deprecationMessage":""},{"name":"_userService","type":"UiUserService","deprecated":false,"deprecationMessage":""},{"name":"_breakpointObserver","type":"BreakpointObserver","deprecated":false,"deprecationMessage":""},{"name":"_router","type":"Router","deprecated":false,"deprecationMessage":""},{"name":"_menuService","type":"UiMenuService","deprecated":false,"deprecationMessage":""},{"name":"_location","type":"Location","deprecated":false,"deprecationMessage"